FROM REMT3100 UNION ALL SELECT DPOB_CD , CLUT_YR_MNTH , CALC_SEVE_PSN_BLGG_YR_MNTH , CALC_SEVE_PAY_PSN_DIV_CD , SYSTEMKEY , (1) AS ORD , ('신고대상세액') AS CALC_SEVE_PAY_PSN_DIV_NM , PYMT_REG_TGT_INCM_TX_QNTY , PYMT_REG_TGT_RGON_INCM_QNTY , PYMT_REG_TGT_FARVIL_SPCL_QNTY , PYMT_REGRST_TGT_AGGR_SUM FROM REMT3100) AA WHERE AA.DPOB_CD=#dpobCd# AND AA.CLUT_YR_MNTH=#clutYrMnth# AND AA.CALC_SEVE_PAY_PSN_DIV_CD=#calcSevePayPsnDivCd# AND AA.SYSTEMKEY=#systemkey# ORDER BY AA.ORD ASC
) A WHERE ROWNUM <= #lastIndex# )WHERE RNUM >= #firstIndex#
