DELETE FROM YE165020 AA WHERE DPOB_CD=#dpobCd# AND YRTX_BLGG_YR=#yrtxBlggYr# AND CLUT_SEPT_CD=#clutSeptCd# AND SYSTEMKEY=#systemkey# ;
