SELECT ROW_NUMBER() OVER(ORDER BY (SELECT LPAD(DEPT_RANK,5,'0') FROM BASS0400 NN WHERE NN.DEPT_CD = A.CURR_AFFN_DEPT_CD) ,A.EMYMT_DIV_CD,A.TYP_OCCU_CD, A.PYSP_GRDE_CD,A.RESN_REGN_NUM ) AS RNUM ,FCBASS0400CDNM (A.DPOB_CD, A.CURR_AFFN_DEPT_CD, '2') AS CURR_DEPT_NM ,FCBASS0400CDNM (A.DPOB_CD, A.DEPT_CD, '2') AS DEPT_NM ,FCBASS0300CDNM(A.DPOB_CD, A.EMYMT_DIV_CD) as EMYMT_DIV_NM ,A.HAN_NM ,SUBSTR(A.RESN_REGN_NUM,0,6)||'-'||SUBSTR(A.RESN_REGN_NUM,7) AS RESN_REGN_NUM ,(fcBass0300CDNM(A.DPOB_CD,A.TYP_OCCU_CD) || DECODE(NVL(A.PYSP_GRDE_CD,'A0060000'), 'A0060000','','[' || fcBass0300CDNM(A.DPOB_CD,A.PYSP_GRDE_CD) || ']' )) AS TYPE_OCCU_NM ,FCBASS0350CDNM(A.DPOB_CD, A.TYP_OCCU_CD,A.DTIL_OCCU_CLS_DIV_CD, '1' ) as DTIL_OCCU_CLS_DIV_NM ,FCBASS0500CDNM (A.DPOB_CD, SUBSTR (A.EMYMT_BGNN_DT, 1, 4), A.CURR_AFFN_DEPT_CD, A.BUSIN_CD) AS BUSIN_NM ,FCBASS0300CDNM(A.DPOB_CD, A.HDOFC_CODTN_CD) as HDOFC_CODTN_NM ,DECODE(LENGTH(A.FRST_EMYMT_DT), 8, TO_CHAR(TO_DATE(A.FRST_EMYMT_DT,'YYYYMMDD'),'YYYY.MM.DD'),A.FRST_EMYMT_DT) AS FRST_EMYMT_DT ,DECODE(LENGTH(A.CURR_DEPT_APPMT_DT), 8, TO_CHAR(TO_DATE(A.CURR_DEPT_APPMT_DT,'YYYYMMDD'),'YYYY.MM.DD'),A.CURR_DEPT_APPMT_DT) AS CURR_DEPT_APPMT_DT ,DECODE(LENGTH(A.RETRY_DT), 8, TO_CHAR(TO_DATE(A.RETRY_DT,'YYYYMMDD'),'YYYY.MM.DD'),A.RETRY_DT) AS RETRY_DT ,FCBASS0300CDNM(A.DPOB_CD,A.LOG_SVC_YR_NUM_CD) AS LOG_SVC_YR_NUM_NM ,FCBASS0300CDNM(A.DPOB_CD,A.LOG_SVC_MNTH_ICM_CD ) AS LOG_SVC_MNTH_ICM_NM ,FCBASS0300CDNM(A.DPOB_CD,B.DABTY_DIV_CD) AS DABTY_DIV_NM ,C.HUS_ROAD_NM_FNDTN_ADDR || C.HUS_ROAD_NM_DTL_PATR_ADDR AS ADDR ,C.HUS_PHN_NUM ,C.CELL_PHNE_NUM ,C.EMAIL_ADDR ,A.PERN_NOTE_CTNT ,A.SYSTEMKEY FROM PSNL0100 A, PSNL0113 B, PSNL0111 C WHERE A.DPOB_CD = B.DPOB_CD(+) AND A.SYSTEMKEY = B.SYSTEMKEY(+) AND A.DPOB_CD = C.DPOB_CD(+) AND A.SYSTEMKEY = C.SYSTEMKEY(+)
AND EXISTS ( SELECT C.DPOB_CD, C.DEPT_CD FROM ( SELECT C.* FROM ( SELECT CC.DPOB_CD, CC.DEPT_CD, CC.DEPT_NM_HAN, CC.DEPT_NM_RTCHNT, CC.DEPT_NM_ENG, CC.TPLVL_DEPT_CD, (CASE WHEN CC.PAYR_MANG_DEPT_CD IN ('405030200') AND CC.DEPT_CD = '405030200' THEN (CASE WHEN CC.HHRK_DEPT_CD = '000000000' THEN '' ELSE CC.HHRK_DEPT_CD END) ELSE CC.HHRK_DEPT_CD END) AS HHRK_DEPT_CD, CC.PAYR_MANG_DEPT_CD, CC.DEPT_USE_YN, CC.NAT_PENN_SYM, CC.HLTH_INSR_SYM, CC.UMYT_INSR_SYM, CC.IDTL_ACCDT_INSUR_SYM, CC.DEPT_NOTE_CTNT, CC.DEPT_DSPY_YN, CC.MANGE_DEPT_CD, CC.DEPT_RANK FROM BASS0400 CC WHERE CC.DEPT_USE_YN = 'Y'
CC.PAYR_MANG_DEPT_CD=#payrMangDeptCd#
) C START WITH C.HHRK_DEPT_CD = (DECODE(#payrMangDeptCd#,'405030200','405000000','000000000')) CONNECT BY PRIOR C.DEPT_CD = C.HHRK_DEPT_CD ) C WHERE C.DPOB_CD = A.DPOB_CD AND C.DEPT_CD = A.CURR_AFFN_DEPT_CD
C.DEPT_CD = #searchCurrDeptCd#
) ORDER BY A.DPOB_CD ASC , A.RESN_REGN_NUM ASC
