DECLARE BEGIN
UPDATE YE161040 AA SET HIFE_DDC_TRGT_AMT = NVL((CASE WHEN NVL((SELECT SUM(NVL(HIFE_DDC_TRGT_AMT,0)) FROM YE161040 BB WHERE BB.DPOB_CD=AA.DPOB_CD AND BB.YRTX_BLGG_YR=AA.YRTX_BLGG_YR AND BB.CLUT_SEPT_CD=AA.CLUT_SEPT_CD AND BB.SYSTEMKEY=AA.SYSTEMKEY AND BB.CD_VVAL_KRN_CD = 'Y0140000' ),0) > 0 THEN (SELECT (NVL(HTHR_HIFE_AMT,0) + NVL(MCUR_HIFE_AMT,0) + NVL(MCUR_HIFE_AREA_AMT,0) ) /* 보험료_건강고용등외 */ FROM YE161060 WHERE YE161060.DPOB_CD = AA.DPOB_CD AND YE161060.YRTX_BLGG_YR = AA.YRTX_BLGG_YR AND YE161060.CLUT_SEPT_CD = AA.CLUT_SEPT_CD AND YE161060.SYSTEMKEY = AA.SYSTEMKEY ) - NVL((SELECT SUM(NVL(HIFE_DDC_TRGT_AMT,0)) FROM YE161040 BB WHERE BB.DPOB_CD=AA.DPOB_CD AND BB.YRTX_BLGG_YR=AA.YRTX_BLGG_YR AND BB.CLUT_SEPT_CD=AA.CLUT_SEPT_CD AND BB.SYSTEMKEY=AA.SYSTEMKEY AND BB.CD_VVAL_KRN_CD = 'Y0140000' ),0)
ELSE NVL((SELECT (NVL(HTHR_HIFE_AMT,0) + NVL(MCUR_HIFE_AMT,0) + NVL(MCUR_HIFE_AREA_AMT,0) ) /* 보험료_건강고용등외 */ FROM YE161060 WHERE YE161060.DPOB_CD = AA.DPOB_CD AND YE161060.YRTX_BLGG_YR = AA.YRTX_BLGG_YR AND YE161060.CLUT_SEPT_CD = AA.CLUT_SEPT_CD AND YE161060.SYSTEMKEY = AA.SYSTEMKEY ),0) END),0) WHERE AA.DPOB_CD=#dpobCd# AND AA.YRTX_BLGG_YR=#yrtxBlggYr# AND AA.CLUT_SEPT_CD=#clutSeptCd# AND AA.CD_VVAL_KRN_CD = 'Y0140001' AND AA.SYSTEMKEY=#systemkey# AND AA.TXPR_DSCM_NO_CNTN = ( SELECT TXPR_DSCM_NO_CNTN FROM YE161030 WHERE YE161030.DPOB_CD=#dpobCd# AND YE161030.YRTX_BLGG_YR=#yrtxBlggYr# AND YE161030.CLUT_SEPT_CD=#clutSeptCd# AND YE161030.SYSTEMKEY=#systemkey# AND YE161030.FAMY_RELA_DIV_CD = 'A0190001' ) ;
UPDATE YE161040 SET MCUR_UI_TRGT_AMT = NVL((SELECT ( NVL(HTHR_UI_AMT,0) + NVL(MCUR_UI_AMT,0)) /* 고용보험_외 */ FROM YE161060 WHERE YE161060.DPOB_CD = YE161040.DPOB_CD AND YE161060.YRTX_BLGG_YR = YE161040.YRTX_BLGG_YR AND YE161060.CLUT_SEPT_CD = YE161040.CLUT_SEPT_CD AND YE161060.SYSTEMKEY = YE161040.SYSTEMKEY ),0) WHERE YE161040.TXPR_DSCM_NO_CNTN = ( SELECT TXPR_DSCM_NO_CNTN FROM YE161030 WHERE YE161030.DPOB_CD=#dpobCd# AND YE161030.YRTX_BLGG_YR=#yrtxBlggYr# AND YE161030.CLUT_SEPT_CD=#clutSeptCd# AND YE161030.SYSTEMKEY=#systemkey# AND YE161030.FAMY_RELA_DIV_CD = 'A0190001' ) AND YE161040.DPOB_CD=#dpobCd# AND YE161040.YRTX_BLGG_YR=#yrtxBlggYr# AND YE161040.CLUT_SEPT_CD=#clutSeptCd# AND YE161040.CD_VVAL_KRN_CD = 'Y0140001' /* 국세청외 */ AND YE161040.SYSTEMKEY=#systemkey# ;
END;

DECLARE BEGIN UPDATE YE161040 AA SET CONB_DDC_TRGT_AMT = NVL( (SELECT SUM(NVL(BB.USE_SUM_AMT,0)) FROM YE165010 BB WHERE BB.DPOB_CD=AA.DPOB_CD AND BB.YRTX_BLGG_YR=AA.YRTX_BLGG_YR AND BB.CLUT_SEPT_CD=AA.CLUT_SEPT_CD AND BB.SYSTEMKEY=AA.SYSTEMKEY AND BB.YRS_MATE_CL_CD = AA.CD_VVAL_KRN_CD AND BB.CONB_CHG_CD NOT IN ('31','50') AND BB.RESNO_ENC_CNTN = AA.TXPR_DSCM_NO_CNTN ) ,0)
, ISMT=#ismt# , REVN_DT=TO_CHAR(SYSDATE,'YYYYMMDDHHMISS') , REVN_ADDR=#revnAddr# WHERE AA.DPOB_CD=#dpobCd# AND AA.YRTX_BLGG_YR=#yrtxBlggYr# AND AA.CLUT_SEPT_CD=#clutSeptCd# AND AA.SYSTEMKEY=#systemkey# AND AA.CD_VVAL_KRN_CD = 'Y0140000';
UPDATE YE161040 AA SET CONB_DDC_TRGT_AMT = NVL( (SELECT SUM(NVL(BB.USE_SUM_AMT,0)) FROM YE165010 BB WHERE BB.DPOB_CD=AA.DPOB_CD AND BB.YRTX_BLGG_YR=AA.YRTX_BLGG_YR AND BB.CLUT_SEPT_CD=AA.CLUT_SEPT_CD AND BB.SYSTEMKEY=AA.SYSTEMKEY AND BB.CONB_CHG_CD NOT IN ('31','50') AND BB.YRS_MATE_CL_CD = AA.CD_VVAL_KRN_CD AND BB.RESNO_ENC_CNTN = AA.TXPR_DSCM_NO_CNTN ) ,0)
, ISMT=#ismt# , REVN_DT=TO_CHAR(SYSDATE,'YYYYMMDDHHMISS') , REVN_ADDR=#revnAddr# WHERE DPOB_CD=#dpobCd# AND YRTX_BLGG_YR=#yrtxBlggYr# AND CLUT_SEPT_CD=#clutSeptCd# AND SYSTEMKEY=#systemkey# AND AA.CD_VVAL_KRN_CD = 'Y0140001'; END;
