sqlMap.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT DISTINCT AA.SYS_DIV_CD , AA.MNU_CD , AA.HHRK_MNU_CD , AA.MNU_NM , AA.MNU_ICN , AA.MNU_TMBL , AA.MNU_AUTH_FRM_CD , AA.MNU_TIP_NM , AA.MNU_SCN_FRM_PKG_NM , AA.MNU_PRNT_YN , AA.MNU_PRNT_ORD , AA.USE_YN , AA.MNU_EPLN , AA.KYBDR , AA.INPT_DT , AA.ISMT , AA.REVN_DT , AA.REVN_ADDR , AA.INPT_ADDR , BB.MNU_AUTH_GR_P_SCN_INQY_YN , BB.MNU_AUTH_GR_P_SCN_INPT_YN , BB.MNU_AUTH_GR_P_SCN_REVN_YN , BB.MNU_AUTH_GR_P_SCN_DEL_YN , BB.MNU_AUTH_GR_P_SCN_PRT_YN FROM SYSM0200 AA ,SYSM0400 BB WHERE 1=1 AND AA.SYS_DIV_CD = BB.SYS_DIV_CD AND AA.MNU_CD = BB.MNU_CD ORDER BY AA.MNU_PRNT_ORD ASC , AA.MNU_CD ASC
) A WHERE ROWNUM <= #lastIndex# )WHERE RNUM >= #firstIndex#
) A )
